for(let e=0;e<$(".post-comments-submit").length;e++)$(".post-comments-submit").eq(e).on("click",(function(e){e.preventDefault(),$.ajax({method:"POST",url:"/comments/create",data:$(".comments-form").eq(0).serialize(),success:function(e){let t=newCommentDom(e.data.comment);$("#post-comments-"+e.data.comment.post).prepend(t),$("#comment-val").val(""),new Noty({theme:"relax",text:"Comment published!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(error.responseText),new Noty({theme:"danger",text:"Error in publishing comment :3",type:"failure",layout:"topRight",timeout:1500}).show()}})}));function newCommentDom(e){return $(`<li id="comment-${e._id}">\n                    <p>\n                        \n                        <small>\n                            <a class="delete-comment-button" href="/comments/destroy/${e._id}">X</a>\n                        </small>\n                        \n                        ${e.content}\n                        <br>\n                        <small>\n                            ${e.user.name}\n                        </small>\n                    </p>    \n\n            </li>`)}